    @Test
    public void deploy() throws IOException, TransactionException {
        Caver caver = new Caver(Caver.DEFAULT_URL);
        caver.wallet.add(KeyringFactory.createFromPrivateKey("0x2359d1ae7317c01532a58b01452476b796a3ac713336e97d8d3c9651cc0aecc3"));

        String name = "KlayTN";
        String symbol = "KCT";
        BigInteger decimals = BigInteger.valueOf(18);
        BigInteger initialSupply = BigInteger.valueOf(100_000).multiply(BigInteger.TEN.pow(decimals.intValue())); // 100000 * 10^18

        List<Type> deployParams = Arrays.asList(
                new Utf8String(name),
                new Utf8String(symbol),
                new Uint8(decimals),
                new Uint(initialSupply)
        );


        SendOptions options = new SendOptions(LUMAN.getAddress(), DefaultGasProvider.GAS_LIMIT);
        ContractDeployParams contractDeployParam = new ContractDeployParams(BINARY, deployParams);

        Contract contract = new Contract(caver, jsonObj);
        contract.deploy(contractDeployParam, options);

        System.out.println(contract.getContractAddress());
    }

